// src/components/EmailForm.js
import React, { useState } from 'react';
import './Form.css';

const EmailForm = () => {
  const [subject, setSubject] = useState('');
  const [tone, setTone] = useState('');
  const [response, setResponse] = useState(null); // State for API response

  // Placeholder for the API request
  const handleSubmit = (e) => {
    e.preventDefault();

    // Define the API endpoint and request data
    const apiEndpoint = 'https://api.example.com/send-email'; // Replace with actual API endpoint
    const requestData = { subject, tone };

    // This is where you'll add the API request later
    console.log("Form submitted:", { subject, tone });

    // Uncomment and use fetch or axios for the actual API call

    fetch(apiEndpoint, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(requestData),
    })
    .then(response => response.json())
    .then(data => console.log("API response:", data))
    .catch(error => console.error("API error:", error));

  };

  return (
    <form className="email-form" onSubmit={handleSubmit}>
      <div className="form-group">
        <label htmlFor="subject">Subject:</label>
        <input
          type="text"
          id="subject"
          name="subject"
          value={subject}
          onChange={(e) => setSubject(e.target.value)}
          placeholder="Enter email subject"
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="tone">Tone:</label>
        <select
          id="tone"
          name="tone"
          value={tone}
          onChange={(e) => setTone(e.target.value)}
          required
        >
          <option value="">Select tone</option>
          <option value="Formal">Formal</option>
          <option value="Casual">Casual</option>
          <option value="Friendly">Friendly</option>
          <option value="Persuasive">Persuasive</option>
          <option value="Concise">Concise</option>
          <option value="Empathetic">Empathetic</option>
          <option value="Neutral">Neutral</option>
          <option value="Encouraging">Encouraging</option>
        </select>
      </div>

      <button type="submit" className="submit-button">Submit</button>
    </form>
  );
};

export default EmailForm;
