// src/App.js
import React, { useState } from "react";
import { Routes, Route, Link } from "react-router-dom"; // Import routing components
import Signup from "./components/Signup";
import Login from "./components/Login";
import EmailForm from "./components/EmailForm";

const App = () => {
  // Track if user is logged in
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Function to handle the login action
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  return (
    <div>
      <Routes>
        {/* Route to SignUp component */}
        <Route path="/signup">
          <Signup />
        </Route>

        {/* Route to Login component */}
        <Route path="/login">
          <Login onLogin={handleLogin} />
        </Route>

        {/* Route to EmailForm when logged in */}
        <Route path="/email-form">
          {isLoggedIn ? <EmailForm /> : <Link to="/login">Please log in</Link>}
        </Route>

        {/* Default route (Home page or first page) */}
        <Route path="/" exact>
          <div className="home-page">
            <h2>Welcome to EmailAI</h2>
            <Link to="/signup">Sign Up</Link>
          </div>
        </Route>
      </Routes>
    </div>
  );
};

export default App;
