// src/App.js
import React, { useState } from "react"; // Import React and useState hook
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";  // Import React Router components
import Login from "./components/Login"; // Import Login component
import EmailForm from "./components/EmailForm"; // Import EmailForm component
import Signup from "./components/Signup";  // Import Signup component


const App = () => {
  // Track if user is logged in
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Function to handle the login action
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  return (
    <Router> {/* Wrap the app in Router */}
      <div>
        <Switch>
          {/* Define the routes */}
          <Route exact path="/login">
            <Login onLogin={handleLogin} />
          </Route>
          <Route exact path="/signup">
            <Signup />
          </Route>
          {/* Default Route (root route) should show the Signup page */}
          <Route exact path="/">
            <Signup />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
